import React, { useState } from 'react';
import { IBook } from '../interfaces/book.interface';
import useForm from '../hooks/useForm';

interface BookFormProps {
    onAddBook: (book: IBook) => void;
}

const   BookForm = ({ onAddBook }: BookFormProps) => {
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState<string>('');
    const [year, setYear] = useState<number>();

    const {validate} = useForm((values) => {
        const errors: Record<string, string> = {};
        if (!values.title) errors.title = 'Title is required';
        if (!values.author) errors.author = 'Author is required';
        if (!values.year) errors.year = 'Year is required';
        return errors;
    });

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        validate({title, author, year});
        if (title && author && year) {
            const newBook: IBook = {
                id: Date.now(),
                title,
                author,
                year: Number(year),
            };
            onAddBook(newBook);
            setTitle('');
            setAuthor('');
            setYear(undefined);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label htmlFor="title">TÃ­tulo:</label>
                <input
                    type="text"
                    id="title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
            </div>
            <div>
                <label htmlFor="author">Autor:</label>
                <input
                    type="text"
                    id="author"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                    required
                />
            </div>
            <div>
                <label htmlFor="year">Ano:</label>
                <input
                    type="number"
                    id="year"
                    value={year}
                    onChange={(e) => setYear(e.target.valueAsNumber)}
                    required
                />
            </div>
            <button type="submit">Cadastrar Livro</button>
        </form>
    );
};

export default BookForm;
