import React, { useCallback, useEffect, useMemo, useState } from 'react';
import { IBook } from '../interfaces/book.interface';
import PotterApi from '../services/potterApi';
interface BookListProps {
    books: IBook[];
}

const BookList = () => {
    const potterService = useMemo(() => new PotterApi(), []);
    const [books, setBooks] = useState<IBook[]>([]);

    const handleGetBooks = useCallback(async () => {
        const response: any = await potterService.getBooks();

        const listBooks = response.map((item: any) => {
            return {
                title: item.title,
                id: item.number,
                author: 'J. K. Rowling'
            }
        });

        setBooks(listBooks)
    }, []);
    

    useEffect(() => {
        handleGetBooks();
    }, [handleGetBooks]);

    return (
        <div>
            <h2>Lista de Livros</h2>
            <ul>
                {books.map((book) => (
                    <li key={book.id}>
                        <strong>{book.title}</strong> - {book.author} ({book.year})
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default BookList;
