import axios, { AxiosInstance } from "axios";

class PotterApi{
    private url : string;
    private api: AxiosInstance

    constructor(){
        this.url = 'https://potterapi-fedeperin.vercel.app/en/spells';

        this.api = axios.create({
            baseURL: this.url,
            headers: {
                'Access-Control-Allow-Origin': '*',
            },
        })
    }

    async getBooks(){
        const response = await this.api.get('/en/books');
        return response
    }

}
export default PotterApi;
